/* eslint-disable react-hooks/rules-of-hooks */
/* eslint-disable no-undef */
import React, { Component, useState } from 'react'
import { useNavigate, Link } from 'react-router-dom';
import axios from 'axios';
import './regis_style.css';
class Register extends Component {
    render() {

        const history = useNavigate();

        const [email, setEmail] = useState('')
        
        const [password, setPassword] = useState('')
    
        async function submit(e){
            e.preventDefault();
    
            try{
                await axios.post("http://localhost:8000/login",{
                    email,password
                })


                .then(res=>{
                    if(res.data == "exist"){
                        alert("User already exists")
                    
                    }
                    else if(res.data == "notexist"){
                        history("/home")
                        }
                })
                .catch(e => {
                    alert("Wrong Details")
                    console.log(e)
                })

            }
            catch(e){
                console.log(e);
            }
        }
    
        return (
            <>

    <div className="container register">
                <div className="row">
                    <div className="col-md-3 register-left">
                        <img src="https://image.ibb.co/n7oTvU/logo_white.png" alt=""/>
                        <h3>Welcome</h3>
                        <p>You are 30 seconds away from buying or selling your property!</p>
                        <Link to="/login"><input type="submit" name="" value="Login"/></Link><br/>
                    </div>
                    <div className="col-md-9 register-right">
                        <div className="tab-content" id="myTabContent">
                            <div className="tab-pane fade show active" id="home" role="tabpanel" aria-labelledby="home-tab">
                                <h3 className="register-heading">Registeration</h3>
                            <form>
                                <div className="row register-form">
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            {/* <input type="text" className="form-control" placeholder="First Name *"/> */}
                                        </div>
                                        <div className="form-group">
                                            {/* <input type="text" className="form-control" placeholder="Last Name *"/> */}
                                        </div>
                                        <div className="form-group">
                                            <input type="password" className="form-control" placeholder="Password *"
                                            onChange={(e)=>{ setPassword(e.target.value) }}/>
                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <input type="email" className="form-control" placeholder="Your Email *" 
                                            onChange={(e)=>{ setEmail(e.target.value) }}/>
                                        </div>
                                        <div className="form-group">
                                            {/* <input type="text" minlength="10" maxlength="10" name="txtEmpPhone" className="form-control" placeholder="Your Phone *"/> */}
                                        </div>
                                        <div className="form-group">
                                            {/* <select className="form-control">
                                                <option className="hidden"  selected disabled>Select the Panel Type</option>
                                                <option>Agent</option>
                                                <option>User</option>
                                                
                                            </select> */}
                                        </div>
                                        <Link to="/"><input type="submit" className="btnRegister" onClick={submit}  value="Register"/></Link>
                                    </div>
                                </div>
                                </form>
                            </div>
                        </div>
                    </div>
                </div>

    </div>









            </>
        )
    }
}

export default Register;
